export function PricingFeatureComparison() {
  return (
    <div className="flex flex-col xl:flex-row xl:space-x-12 xl:h-[448px] w-full max-w-md xl:max-w-none xl:w-auto">
      <ul className="flex-col hidden h-full pt-24 xl:flex justify-evenly">
        {features.map((feature) => (
          <li className="py-2 text-xl leading-8" key={feature}>
            {feature}
          </li>
        ))}
        <li className="text-xl leading-8">Cost</li>
      </ul>

      <PricingFeatureComparisonColumn
        title={<PricingFeatureComparisonDawnLogo />}
        featuresChecked={[0, 1, 2, 3, 4]}
        cost="$0-75*"
        isHighlighted
      />
      <PricingFeatureComparisonColumn
        title={<span className="text-xl font-semibold">Sleep Clinics</span>}
        featuresChecked={[0, 3]}
        cost="1000/mo"
      />
      <PricingFeatureComparisonColumn
        title={<span className="text-xl font-semibold">Sleep Therapist</span>}
        featuresChecked={[0, 1, 3, 4]}
        cost="300/mo"
      />
      <PricingFeatureComparisonColumn
        title={<span className="text-xl font-semibold">Sleep Medication</span>}
        featuresChecked={[]}
        cost="100/mo"
      />
      <PricingFeatureComparisonColumn
        title={<span className="text-xl font-semibold">OTC Sleep Aids</span>}
        featuresChecked={[1]}
        cost="20-150/mo"
      />
    </div>
  );
}

